mapper.select:
SELECT visit_cnt
FROM user_visit
WHERE user_id=#{id}

mapper.update:
UPDATE user_visit
SET visit_cnt = visit_cnt + 1
WHERE user_id=#{id}

mapper.select:
SELECT visit_total
FROM user_visit
WHERE user_id=#{id}

mapper.update:
UPDATE user_visit
SET visit_total = visit_total + 1
WHERE user_id=#{id}
